function [tp, fp, fn, tn] = performEvaluation(ytest, p)tp = 0;fn = 0;fp = 0;tn = 0;for i = 1:length(ytest)  if (p(i)==ytest(i)) && ytest(i) == 1    tp+=1;  end    if (p(i)==ytest(i)) && ytest(i) == 0    tn+=1;  end  if (p(i)!=ytest(i)) && ytest(i) == 1    fn+=1;  end  if (p(i)!=ytest(i)) && ytest(i) == 0    fp+=1;  endendfprintf('\nTP: %d\nFN: %d\nFP: %d\nTN: %d\n', tp, fn, fp, tn);end